    private void cmdSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmdSaveActionPerformed
        try {
            Class.forName("com.mysql.jdbc.Driver");
            Connection con = (Connection)
            DriverManager.getConnection("jdbc:mysql://localhost:3306/test","root","raj");
            Statement stmt = null;
            ResultSet rs = null;
            String SQL = "SELECT * FROM emp";
            stmt = con.createStatement();
            rs = stmt.executeQuery(SQL);
            int no = Integer.parseInt(txtNo.getText());
            String nam = txtPName.getText();
            String city = txtCity.getText();
            String ph = txtPhone.getText();
            char sex='Y';
            if (jRadioButton1.isSelected())
                 sex = 'M';
            else
                sex='F';
            int code = JOptionPane.showConfirmDialog(this, "Are you sure to add?", "Confirmation Dialog Box", JOptionPane.YES_NO_CANCEL_OPTION, JOptionPane.INFORMATION_MESSAGE);
            if (code == JOptionPane.YES_OPTION) {
                String strSQL = "INSERT INTO emp(empID, EmpName, EmpCity, EmpPhone, EmpSex) VALUES ("+(no)+", '"+(nam)+"', '"+(city)+"', '"+(ph)+"', '"+(sex)+"')";
                stmt.executeUpdate(strSQL);
                JOptionPane.showMessageDialog(this, "Record added successfully into Employee table");
            }
            con.close();
            stmt.close();
            rs.close();
            cmdSave.setEnabled(false);
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, e.getMessage());
        }

    }//GEN-LAST:event_cmdSaveActionPerformed

